import React from 'react';
import logo from './logo.svg';
import './App.css';
import Paper from "@material-ui/core/Paper";
import MaterialTable from "material-table";
import TablePagination from "@material-ui/core/TablePagination";

function App() {
  return (
    <Paper>
        <div>
          <MaterialTable
            style={{ width: 1200 }}
            title={this.props.titre}
            columns={this.state.columns}
            data={this.props.data}
            options={{
              search: true,
              exportButton: false,
              paging: false
            }}
            onSearchChange={search => this.search(search)}
            editable={{
              onRowAdd: newData =>
                new Promise(resolve => {
                  resolve();
                  console.log(newData);
                  this.addRestaurant(newData);
                }),
              onRowUpdate: (newData, oldData) =>
                new Promise(resolve => {
                  resolve();
                  this.modifRestaurant(oldData, newData);
                }),
              onRowDelete: oldData =>
                new Promise(resolve => {
                  resolve();
                  this.deleteRestaurant(oldData);
                })
            }}
          />
        </div>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25, 50, 100]}
          component="div"
          count={this.props.nbRestaurant}
          rowsPerPage={this.state.rowsPerPage}
          page={this.state.indexPage}
          backIconButtonProps={{
            "aria-label": "previous page"
          }}
          nextIconButtonProps={{
            "aria-label": "next page"
          }}
          onChangeRowsPerPage={(e, v) => this.setPageSize(e, v)}
          onChangePage={(e, v) => this.setIndexPage(e, v)}
        />
      </Paper>
  );
}

export default App;
